                services: { en: 'Services', de: 'Dienstleistungen', es: 'Servicios', fr: 'Services', id: 'Layanan' },
                work: { en: 'Work', de: 'Arbeit', es: 'Trabajo', fr: 'Travail', id: 'Pekerjaan' }
